export enum QuestType {
    BREAK,
    PLACE,
    CRAFT,
    FISH,
    KILL,
    BREED,
    COLLECT,
    SMELT,
    LOCATE
}

export function getQuestType(value: string): QuestType {
  var type = QuestType[value]; // will return a number because of how TS parses enums
  switch (type) {
    case 0: return QuestType.BREAK;
    case 1: return QuestType.PLACE;
    case 2: return QuestType.CRAFT;
    case 3: return QuestType.FISH;
    case 4: return QuestType.KILL;
    case 5: return QuestType.BREED;
    case 6: return QuestType.COLLECT;
    case 7: return QuestType.SMELT;
    case 8: return QuestType.LOCATE;
  }
}