var errors = require('errors');

/**
 * @typedef {function(number, number)} random
 * @param {Number} min 
 * @param {Number} max 
 * @returns {Number}
 */
function random(min, max) {
    if (min > max) {
        throw new errors.ERR_INVALID_ARG_VALUE('min', min, 'min must be greater than max');
    }
    var dif = max - min;
    var r = Math.random();
    return r * dif;
}
exports = random;