function getWorld(world) {
    if (world instanceof org.bukkit.World) return world;
    return Bukkit.getWorld(world);
}

function getWorlds() {
    return Bukkit.getWorlds();
}

function getWorldType() {
    return Bukkit.getWorldType();
}

function map(world) {
    return Bukkit.createMap(getWorld(world));
}