function getOfflinePlayer(player) {
    if (player.getUniqueId) player = player.getUniqueId();
    return Bukkit.getOfflinePlayer(player);
}

function getPlayer(player) {
    if (player.getUniqueId) player = player.getUniqueId();
    return Bukkit.getPlayer(player);
}

